<?phpinclude('config2.php');include('_mail_fire.php');								$sql1="select  				soum_master_customer.cust_id, soum_master_customer.fname,soum_master_customer.reg_id,soum_master_customer.email,  concat(if ((day(now())=day(dob) and month(now()) =month(dob) ), 'B','') , if ((day(now())=day(doa) and month(now()) =month(doa) ), 'A','')) greet								from soum_master_customer 								where (day(now())=day(dob) and month(now()) =month(dob) ) or (day(now())=day(doa) and month(now()) =month(doa) )				";										$res1=$db->query($sql1);								while ($row1=$res1->fetch_assoc())				{					//echo "<li><a href='mail_greeting.php?type=".$row1['greet']."&tokenid=".$row1['reg_id']."&mail=".$row1['email']."'>".$row1['fname']."(".$row1['greet'].")</a></li>";		                      $type=$row1['greet'];		                      $token=$row1['reg_id'];		                      $mail=$row1['email'];		                      				                if($type=='B')				                {				                 $mailtype=7;				                 $mailsubject="Happy Birthday ";				                }				                else if($type=='A')				                {				                 $mailsubject="Happy Anniversary";                                  $mailtype=8;				                }				                else if($type=='BA')				                {				                 $mailsubject="Happy Birthday & Anniversary";                                  $mailtype=9;				                }				                								$mailtoken=$token;								$mailto=$mail;								mail_reg($mailto,$mailsubject,$mailtype,$mailtoken);					}				    ?>